    function autosubledgeraccount()
    {
        $this->dataoperationmode = operationmode::Select;
        $query = "select c.htcontractorledgercode,a.contractorcode,a.contractornameeng,a.contractornameuni
        from (select t.accountcode,s.prefix||p.contractorcode as referencecode 
                from nst_nasaka_finance.accountheaddetail t,nst_nasaka_finance.subledgertype s
                ,contractor p,agriculturecontroltable c
                where t.subledgertypecode=s.subledgertypecode 
                and t.accountcode=c.HTCONTRACTORLEDGERCODE
                minus
                select t.accountcode,t.referencecode
                from nst_nasaka_finance.accountsubledger t,nst_nasaka_finance.subledgertype s
                ,contractor p,agriculturecontroltable c
                where t.subledgertypecode=s.subledgertypecode 
                and t.accountcode=c.HTCONTRACTORLEDGERCODE)t,contractor a,agriculturecontroltable c
                where t.referencecode='Q'||a.contractorcode 
                order by a.contractorcode";
        $result = oci_parse($this->connection, $query);
        $r = oci_execute($result,OCI_NO_AUTO_COMMIT);
        $connection1=swapp_connection_user('nst_nasaka_finance');
        while ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            $accountsubledger1 = new accountsubledger($connection1);
            $accountsubledger1->subledgernameeng = $row['CONTRACTORNAMEENG'];
            $accountsubledger1->subledgernameuni = $row['CONTRACTORNAMEUNI'];
            $accountsubledger1->subledgertypecode = 21;
            $accountsubledger1->accountcode = $row['HTCONTRACTORLEDGERCODE'];
            $accountsubledger1->referencecode = 'Q'.$row['CONTRACTORCODE'];
            $ret = $accountsubledger1->insert();
            if ($ret == 1)
            {
                oci_commit($connection1);
            }
            else
            {
                oci_rollback($connection1);
            }
        }
    }
