                $vehicle1->fetch();
                $this->vehicle = $vehicle1->vehiclenumber;
                $tyregadi1 = new tyregadi($this->connection);
                $tyregadi1->seasoncode = $this->seasoncode;
                $tyregadi1->tyregadicode = $this->tyregadicode;
                $tyregadi1->fetch();
                if ($_SESSION['lng']=="English")
                {
                    $this->tyregadi = $tyregadi1->tyregadinumber.' '.$tyregadi1->gadiwannameeng;
                }
                else
                {
                    $this->tyregadi = $tyregadi1->tyregadinumber.' '.$tyregadi1->gadiwannameuni;
                }
                $slipboy1 = new slipboy($this->connection);
                $slipboy1->slipboycode = $this->slipboycode;
                $slipboy1->fetch();
                if ($_SESSION['lng']=="English")
                {
                    $this->slipboy = $slipboy1->slipboynameeng;
                }
                else
                {
                    $this->slipboy = $slipboy1->slipboynameuni;
                }

                $this->found=true;
                return true;
        }
        else
        {
        return false;
        }
    }
}
?>

