         <script>
        function myfunction()
        {
            if (document.getElementById("weightcategorycode").value==1)
            {
                document.getElementById("loadweight").value=document.getElementById("weight").value
            }
            else if (document.getElementById("weightcategorycode").value==2)
            {
                document.getElementById("emptyweight").value=document.getElementById("weight").value
            }
            tonnagecal();
            document.getElementById("weight").focus();
        }
        </script>
        <script>
            function tonnagecal()
            {
                var loadweight = Number(document.getElementById("loadweight").value);
                var emptyweight = Number(document.getElementById("emptyweight").value);
                if (loadweight == "")
                    loadweight = 0;
                if (emptyweight == "")
                    emptyweight = 0; 
                var bindingnarration=0;
                var grossweight=0;
                var netweight=0;
                if (loadweight<0)
                {
                    loadweight=0;
                    emptyweight=0;
                }
                if (emptyweight<0)
                {
                    loadweight=0;
                    emptyweight=0;
                }
                if (loadweight>emptyweight && emptyweight>0)
                {
                    netweight=Number(loadweight-emptyweight).toFixed(3);
                }
                else
                {
                    netweight=0;
                }
                if (!isNaN(netweight)) 
                { 
                    document.getElementById('netweight').value = netweight;
                }
            }
        </script>
