        <script>
            function cal()
            {
                var quantity = Number(document.getElementById("quantity").value);
                var purchaserate = Number(document.getElementById("purchaserate").value);
                var salerate = Number(document.getElementById("salerate").value);
                if (quantity == "")
                    quantity = 0;
                if (purchaserate == "")
                    purchaserate = 0;
                if (salerate == "")
                    salerate = 0;

                var purchaseamount=0;
                var saleamount=0;
                purchaseamount = Number((purchaserate*quantity).toFixed(2));
                saleamount = Number((salerate*quantity).toFixed(2));
                if (!isNaN(purchaseamount)) 
                { 
                    document.getElementById('purchaseamount').value = purchaseamount;
                }
                if (!isNaN(saleamount)) 
                { 
                    document.getElementById('saleamount').value = saleamount;
                }
            }
        </script>
