    function checkrecordexists(&$connection,$owner,$tablename)
    {
        $query = "select 
        nvl(count(*),0) as cnt
        from nst_nasaka_db.swapp_table_info 
        where 
        owner ='".strtoupper($owner)."'
        and tablename ='".strtoupper($tablename)."'";
        $result = oci_parse($connection, $query);
        $r = oci_execute($result);
        if ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            if ($row['CNT']>0)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        else
        {
            return false;
        }
    }
    function checkcolumnupdated(&$connection,$owner,$tablename)
    {
        $query = "select sum(cnt) as cnt,sum(allcnt) as allcnt
        from (
        select nvl(count(*),0) as cnt,0 as allcnt
        from nst_nasaka_db.swapp_table_info 
        where engdesc is not null and unicodedesc is not null
        and owner ='".strtoupper($owner)."'
        and tablename ='".strtoupper($tablename)."' 
        union all
        select 
        0 as cnt,count(*) as allcnt
        from nst_nasaka_db.swapp_table_info 
        where 
        owner ='".strtoupper($owner)."'
        and tablename ='".strtoupper($tablename)."')";
        $result = oci_parse($connection, $query);
        $r = oci_execute($result);
        if ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            if ($row['ALLCNT']-$row['CNT']==0)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        else
        {
            return false;
        }
    }
