        function username($userid)
        {
            require("../info/phpsqlajax_dbinfo.php");
            //require_once("../info/phpgetloginview.php");
            //require_once("../info/routine.php");
            $connection1=mysqli_connect($hostname, $username, $password, $database);
            // Check connection
            if (mysqli_connect_errno())
            {
                echo "Communication Error";
                exit;
            }
            if ($userid!="")
            {
                $query = "select u.misuserid,
                u.misusername
                from misuserresponsibility m,misuser u 
                where m.misuserid=u.misuserid
                and misresponsibilityid=123980930 
                and u.misuserid=".$userid;
                $result = mysqli_query($connection1,$query);
                if (!$result) 
                {
                    //echo $query;
                    die('Communication Error');
                }
                if ($row = @mysqli_fetch_assoc($result))
                {
                    $id = $row['misuserid'];
                    if ($_SESSION['lng'] == "English")
                    {
                        $name = $row['misusername'];
                    }
                    else
                    {
                        $name = $row['misusername'];
                    }
                }
            }
            else
            {
                $name="";
            }
            return $name;
        }
