        <script>
            function tonnagecal()
            {
                var loadweight = Number(document.getElementById("loadweight").value);
                var emptyweight = Number(document.getElementById("emptyweight").value);
                if (loadweight == "")
                    loadweight = 0;
                if (emptyweight == "")
                    emptyweight = 0; 
                var bindingmaterial=0;
                var netweight=0;
                if (loadweight<0)
                {
                    loadweight=0;
                    emptyweight=0;
                }
                if (emptyweight<0)
                {
                    loadweight=0;
                    emptyweight=0;
                }
                if (loadweight>emptyweight && emptyweight>0)
                {
                    bindingmaterial=Number((loadweight-emptyweight)*0.01).toFixed(3);
                    netweight=Number(loadweight-emptyweight-bindingmaterial).toFixed(3);
                }
                else
                {
                    bindingmaterial=0;
                    netweight=0;
                }

                if (!isNaN(bindingmaterial)) 
                { 
                    document.getElementById('bindingmaterial').value = bindingmaterial;
                } 
                if (!isNaN(netweight)) 
                { 
                    document.getElementById('netweight').value = netweight;
                }
            }
        </script>
