        <script>
            function qtycal()
            {
                var salequantity = Number(document.getElementById("salequantity").value);
                var salerate = Number(document.getElementById("salerate").value);
                var sgstrate = Number(document.getElementById("sgstrate").value);
                var cgstrate = Number(document.getElementById("cgstrate").value);
                var igstrate = Number(document.getElementById("igstrate").value);
                var ugstrate = Number(document.getElementById("ugstrate").value);
                var vatrate = Number(document.getElementById("vatrate").value);
                var tcsrate = Number(document.getElementById("tcsrate").value);
                if (salequantity == "")
                    salequantity = 0;
                if (salerate == "")
                    salerate = 0; 
                if (sgstrate == "")
                    sgstrate = 0;
                if (cgstrate == "")
                    cgstrate = 0;            
                if (igstrate == "")
                    igstrate = 0;
                if (ugstrate == "")
                    ugstrate = 0;
                if (vatrate == "")
                    vatrate = 0;
                if (tcsrate == "")
                    tcsrate = 0;    
                var amount=0;
                var taxableamount=0;
                var sgstamount=0;
                var cgstamount=0;
                var igstamount=0;
                var ugstamount=0;
                var vatamount=0;
                var totaltaxamount=0;
                var grossamount=0;
                var tcsamount=0;
                totaltaxrate = sgstrate+cgstrate+igstrate+ugstrate+vatrate;
                taxableamount = Number((salerate*salequantity).toFixed(2));
                sgstamount = Number((taxableamount*sgstrate/100).toFixed(2));
                cgstamount = Number((taxableamount*cgstrate/100).toFixed(2));
                igstamount = Number((taxableamount*igstrate/100).toFixed(2));
                ugstamount = Number((taxableamount*ugstrate/100).toFixed(2));
                vatamount = Number((taxableamount*vatrate/100).toFixed(2));
                tcsamount= Number(((taxableamount+sgstamount+cgstamount+igstamount+ugstamount+vatamount)*tcsrate/100).toFixed(2));
                totaltaxamount = sgstamount+cgstamount+igstamount+ugstamount+vatamount;
                grossamount = taxableamount+totaltaxamount+tcsamount;
                
                if (!isNaN(amount)) 
                { 
                    document.getElementById('amount').value = taxableamount;
                } 
                if (!isNaN(sgstamount)) 
                { 
                    document.getElementById('sgstamount').value = sgstamount;
                } 
                if (!isNaN(cgstamount)) 
                { 
                    document.getElementById('cgstamount').value = cgstamount;
                } 
                if (!isNaN(igstamount)) 
                { 
                    document.getElementById('igstamount').value = igstamount;
                } 
                if (!isNaN(ugstamount)) 
                { 
                    document.getElementById('ugstamount').value = ugstamount;
                } 
                if (!isNaN(vatamount)) 
                { 
                    document.getElementById('vatamount').value = vatamount;
                }
                if (!isNaN(tcsamount)) 
                { 
                    document.getElementById('tcsamount').value = tcsamount;
                }
                if (!isNaN(totaltaxamount)) 
                { 
                    document.getElementById('totaltaxamount').value = totaltaxamount;
                } 
                if (!isNaN(grossamount)) 
                { 
                    document.getElementById('grossamount').value = grossamount;
                } 
            }
        </script>
