    function autosubledgeraccount()
    {
        $this->dataoperationmode = operationmode::Select;
        $query = "select t.accountcode,s.prefix||p.purchasercode as referencecode 
        from nst_nasaka_finance.accountheaddetail t,nst_nasaka_finance.subledgertype s,goodspurchaser p
        where t.subledgertypecode=s.subledgertypecode and t.subledgertypecode=3
        minus
        select l.accountcode,l.referencecode
        from nst_nasaka_finance.accountheaddetail t,nst_nasaka_finance.subledgertype s,nst_nasaka_finance.accountsubledger l
        where t.subledgertypecode=s.subledgertypecode and t.subledgertypecode=3 and t.accountcode=l.accountcode";
        $result = oci_parse($this->connection, $query);
        $r = oci_execute($result,OCI_NO_AUTO_COMMIT);
        $connection1=swapp_connection_user('nst_nasaka_finance');
        while ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            $accountsubledger1 = new accountsubledger($connection1);
            $accountsubledger1->subledgernameeng = $this->purchasernameeng;
            $accountsubledger1->subledgernameuni = $this->purchasernameuni;
            $accountsubledger1->subledgertypecode = 3;
            $accountsubledger1->accountcode = $row['ACCOUNTCODE'];
            $accountsubledger1->referencecode = $row['REFERENCECODE'];
            $ret = $accountsubledger1->insert();
            if ($ret == 1)
            {
                oci_commit($connection1);
            }
            else
            {
                oci_rollback($connection1);
            }
        }
