    public function getbalance($customercategorycode,$customercode,$invoicedate)
    {
        $invdt=DateTime::createFromFormat('d/m/Y',$invoicedate)->format('d-M-Y');
        $query = "select nvl(sum(quantity),0) balance
        from (
        select t.yearcode,t.customercategorycode,d.customercode,d.quantity 
        from ALLOTMENTHEADER t,allotmentdetail d,allotmentcontrol c
        where t.transactionnumber=d.transactionnumber and t.allotconttrnnumber=c.transactionnumber 
        and c.alloteddatetime<='".$invdt."' and c.expirydate>='".$invdt."'
        union all
        select tt.yearcode,tt.customercategorycode,tt.customercode,dd.quantity*-1 
        from concessionsaleheader tt,concessionsaledetail dd
        where tt.transactionnumber=dd.transactionnumber
        and tt.invoicedate<='".$invdt."'
        )
        group by yearcode,customercategorycode,customercode
        having yearcode=".$_SESSION['yearperiodcode']." 
        and customercategorycode=".$customercategorycode.
        " and customercode=".$customercode;
        

        $result = oci_parse($this->connection, $query);
        $r = oci_execute($result);
        if ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
