        <script>
            function qtycal()
            {
                var quantity = Number(document.getElementById("quantity").value);
                var rate = Number(document.getElementById("rate").value);
                var bagquantity = Number(document.getElementById("bagquantity").value);
                var bagrate = Number(document.getElementById("bagrate").value);
                if (quantity == "")
                    quantity = 0;
                if (rate == "")
                    rate = 0; 
                if (bagquantity == "")    
                    bagquantity = 0;
                if (bagrate == "")    
                    bagrate = 0;
                var amount=0;
                var totalamount=0;
                amount = Number((rate*quantity).toFixed(2));
                bagcharges = Number((bagrate*bagquantity).toFixed(2));
                totalamount = Number((amount+bagcharges).toFixed(2));
                if (!isNaN(amount)) 
                { 
                    document.getElementById('amount').value = amount;
                }
                if (!isNaN(totalamount)) 
                { 
                    document.getElementById('totalamount').value = totalamount;
                }
            }
        </script>
