    public function updateheader($transactionnumber)
    {
        $sql = 'BEGIN updateinvoiceitemdetail(:p_transactionnumber); END;';
        $result = oci_parse($this->connection,$sql);
        oci_bind_by_name($result,':p_transactionnumber',$transactionnumber,20,SQLT_INT);
        oci_execute($result);
        $query = "select nvl(sum(t.amount),0) amount
        ,nvl(sum(t.discountamount),0) discountamount
        ,nvl(sum(t.discountedamount),0) discountedamount
        ,nvl(sum(t.packingforwardingamount),0) packingforwardingamount
        ,nvl(sum(t.taxableamount),0) taxableamount
        ,nvl(sum(t.totalgstamount),0) totalgstamount
        ,nvl(sum(t.itemtotal),0) itemtotal
        from INVOICEITEMDETAIL t 
        where t.transactionnumber={$transactionnumber}";
        $result = oci_parse($this->connection, $query);
        $r = oci_execute($result);
        if ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            $query = "
            update invoiceheader
            set grossitemtotal={$row['AMOUNT']}
            ,grossdiscountamount={$row['DISCOUNTAMOUNT']}
            ,grossdiscountedamount={$row['DISCOUNTEDAMOUNT']}
