<script> 
            function qtycal()
{
    var grossamount = Number(document.getElementById("grossamount").value);
    var transportation = Number(document.getElementById("transportation").value);
    var tcsamount = Number(document.getElementById("tcsamount").value);
    var roundoff = Number(document.getElementById("roundoff").value);
    if (grossamount == "")
        grossamount = 0;
    if (transportation == "")
        transportation = 0;  
    if (tcsamount == "")
        tcsamount = 0; 
    if (roundoff == "")
        roundoff = 0; 
        
        
    var netamount=0;
    netamount = Number((grossamount+transportation+tcsamount+roundoff).toFixed(2));
    

    if (!isNaN(netamount)) 
    { 
        document.getElementById('netamount').value = netamount;
    }
            }
