    function title(&$connection,$shiftcode,$petrolpumpcode,$pumpcode,$customertypecode)
    {
        $str='';
        if ($shiftcode==1)
        {
            if ($_SESSION['lng']=="English")
            {
                $str = $str.', Shift:First';
            }
            else
            {
                $str = $str.', शिफ्ट:पहिली';
            }
        }
        elseif ($shiftcode==2)
        {
            if ($_SESSION['lng']=="English")
            {
                $str = $str.', Shift:Second';
            }
            else
            {
                $str = $str.', शिफ्ट:दुसरी';
            }
        }
        elseif ($shiftcode==3)
        {
            if ($_SESSION['lng']=="English")
            {
                $str = $str.', Shift:Third';
            }
            else
            {
                $str = $str.', शिफ्ट:तिसरी';
            }
        }
        $query = "select n.namedetailid,n.name_eng,n.name_unicode from nst_nasaka_db.namedetail n 
        where namedetailid=".$petrolpumpcode;
        $result = oci_parse($connection, $query);
        $r = oci_execute($result);
        if ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            if ($_SESSION['lng']=="English")
            {
                $str = $str.'Petrolpump:'.$row['NAME_ENG'];
            }
            else
            {
                $str = $str.'पेट्रोलपंप:'.$row['NAME_UNICODE'];
            }
        }
        $query1 = "select n.pumpcode,n.pumpname_eng,n.pumpname_uni from pump n 
        where pumpcode=".$pumpcode;
        $result1 = oci_parse($connection, $query1);
        $r1 = oci_execute($result1);
        if ($row1 = oci_fetch_array($result1,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            if ($_SESSION['lng']=="English")
            {
                $str = $str.', Pump:'.$row1['PUMPNAME_ENG'];
            }
            else
            {
                $str = $str.', पंप:'.$row1['PUMPNAME_UNI'];
            }
            
        }
        $query = "select n.namedetailid,n.name_eng,n.name_unicode from nst_nasaka_db.namedetail n 
        where namedetailid=".$customertypecode;
        $result = oci_parse($connection, $query);
        $r = oci_execute($result);
        if ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            if ($_SESSION['lng']=="English")
            {
                $str = $str.', Customer Type:'.$row['NAME_ENG'];
            }
            else
            {
                $str = $str.', ग्राहक प्रकार:'.$row['NAME_UNICODE'];
            }
        }
        return $str;
    }
