    function CalculatePolygonArea() 
    {
        $query = "select 
        serialnumber
        ,seasoncode
        ,plotnumber
        ,latitude
        ,longitude
        from plantationplotareadetail where 
         seasoncode = ".$this->seasoncode."
         and plotnumber = ".$this->plotnumber." order by serialnumber
        ";
        $result = oci_parse($this->connection, $query);
        $r = oci_execute($result);
        while ($row = oci_fetch_array($result,OCI_ASSOC+OCI_RETURN_NULLS))
        {
            $latitude[]=floatval($row['LATITUDE']);
            $longitude[]=floatval($row['LONGITUDE']);
        }
        
        $area = 0;
        $coordinatesCount = sizeof($latitude);
        if ($coordinatesCount > 2) 
        {
          for ($i = 0; $i < $coordinatesCount - 1; $i++) 
          {
            $p1Longitude = $longitude[$i];
            $p2Longitude = $longitude[$i+1];
            $p1Latitude = $latitude[$i];
            $p2Latitude = $latitude[$i+1];
            $area += $this->ConvertToRadian($p2Longitude - $p1Longitude) * (2 + sin($this->ConvertToRadian($p1Latitude)) + sin($this->ConvertToRadian($p2Latitude)));
          }
            $p1Longitude = $longitude[$i];
            $p2Longitude = $longitude[0];
            $p1Latitude = $latitude[$i];
            $p2Latitude = $latitude[0];
            $area += $this->ConvertToRadian($p2Longitude - $p1Longitude) * (2 + sin($this->ConvertToRadian($p1Latitude)) + sin($this->ConvertToRadian($p2Latitude)));
            $area = $area * 6378137 * 6378137 / 2;
        }
        else
        {
            $area=0;
        }
        return abs(round(($area)))/10000;
    }
    
